/**
 * Files App UI - HomeChannel
 * ES module that renders file browser interface
 */

// Create UI container
const container = document.createElement('div');
container.id = 'files-app';
container.style.cssText = `
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif;
  padding: 20px;
  height: 100%;
  box-sizing: border-box;
`;

// Header
const header = document.createElement('h2');
header.textContent = 'Files App (Placeholder)';
header.style.margin = '0 0 20px 0';
container.appendChild(header);

// Status message
const status = document.createElement('div');
status.textContent = 'Files app UI loaded successfully! Full file browser UI coming soon.';
status.style.cssText = `
  padding: 10px;
  background: #e3f2fd;
  border-radius: 4px;
  color: #1565c0;
`;
container.appendChild(status);

// Info
const info = document.createElement('div');
info.style.marginTop = '20px';
info.innerHTML = `
  <p>This is a placeholder UI for the files app.</p>
  <p>The full file browser interface will be ported from filebrowser.html.</p>
  <p><strong>Next steps:</strong></p>
  <ul>
    <li>Convert filebrowser.html components to this ES module</li>
    <li>Implement file listing, upload, download UI</li>
    <li>Add datachannel communication with server</li>
  </ul>
`;
container.appendChild(info);

// Append to document body
document.body.appendChild(container);

console.log('Files app UI initialized');
